sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	//only if the user has OnPage user role will they be associated to a record
	if(gs.hasRole('x_gfnll_onpage.user')) {
		var user = current.user;
		var onPageUser = new GlideRecord('x_gfnll_onpage_target');
		onPageUser.addQuery('email', user.email); //gets target email from current users emil
		onPageUser.addQuery('onpage_name', user.user_name);
		onPageUser.query();

		//this associates the queried onPage user with the current user
		if(onPageUser.next()) {
			var u = new GlideRecord('sys_user');
			u.get(user);
			u.email = onPageUser.email;
			u.user_name = onPageUser.onpage_name;
			u.first_name = onPageUser.first_name;
			u.last_name = onPageUser.last_name;
			u.update();
		} //the else will create a new target record with fields from the user table
		else{
			var target = new GlideRecord('x_gfnll_onpage_target');
			target.initialize();
			target.setValue('email',user.email);
			target.setValue('onpage_name',user.user_name);
			target.setValue('first_name',user.first_name);
			target.setValue('last_name',user.last_name);
			target.insert();
		}
	}
	//if user does not have OnPage user role but a target exists associated to current user
	else if(!gs.hasRole('x_gfnll_onpage.user')){
		var noRoleUser = current.user;
		var existingTarget = new GlideRecord('x_gfnll_onpage_target');
		existingTarget.addQuery('email', noRoleUser.email); //grab the target record that is associated to user with noRole
		existingTarget.addQuery('onpage_name', noRoleUser.user_name);
		existingTarget.query();

		//disassociate existing target record from SN record since role is removed
		if(existingTarget.next()) {
			existingTarget.setValue('email', '');
			existingTarget.setValue('onpage_name', '');
			existingTarget.setValue('first_name', '');
			existingTarget.setValue('last_name', '');
			existingTarget.update();
		}
	}
})(current, previous);
