sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var type = '',
		status = current.getValue('status'),
		now = new GlideDateTime(),
		definition = "52226aa4dbe42740ee685e25ca9619fc", //OnPage Notification Send Time
		table = "x_gfnll_onpage_notification",
		field = 'status',
		id = current.getUniqueValue(),
		cc = false,
		value = status,
		end ='';
	if(status=='send-ready'&&current.isNewRecord()){
		type = 'create';
	}
	else{
		type = 'update';
	}
	var gr = new GlideRecord('metric_instance');
	if(type=='create'){
		new x_gfnll_onpage.log().info("Creating metric for "+current.getValue('number'),"internal",current.getRecordClassName(),id);
		gr.initialize();
		gr.setValue('definition',definition);
		gr.setValue('table',table);
		gr.setValue('field',field);
		gr.setValue('id',id);
		gr.setValue('calculation_complete',cc);
		gr.setValue('value',value);
		gr.setValue('start',now);
		gr.setValue('end',end);
		gr.insert();
		new x_gfnll_onpage.log().info("Created metric "+gr.getUniqueValue()+" for "+current.getValue('number'),"internal",current.getRecordClassName(),id);
	}
	if(type=="update"){
		gr.addQuery('definition',definition);
		gr.addQuery('table',table);
		gr.addQuery('field',field);
		gr.addQuery('id',id);
		gr.addQuery('calculation_complete',false);
		gr.query();
		while(gr.next()){
			var start = new GlideDateTime(gr.getValue('start'));
			var duration = new GlideDateTime.subtract(start,now);
			gr.setValue('end',now);
			gr.setValue('duration',duration);
			gr.setValue('calculation_complete',true);
			gr.update();
			new x_gfnll_onpage.log().info("Updated open metric "+gr.getUniqueValue()+" for "+current.getValue('number'),"internal",current.getRecordClassName(),id);
		}
	}
})(current, previous);
