sys_script.script:
(function executeRule(current, previous /*null when async*/) {


	var json = current.current;
	var parser = new JSONParser();
	var eventContent = parser.parse(json);
	var grActualNotification = new GlideRecord('x_gfnll_onpage_notification');
		grActualNotification.addQuery('sys_id', eventContent.status.id);
		grActualNotification.query();

		if (grActualNotification.next())
		{
			var workNotes = gs.getProperty('x_gfnll_onpage.can_update.work_notes');
			if(workNotes)
			{


				var grNotification = new GlideRecord('x_gfnll_onpage_notification');
				grNotification.initialize();						
				grNotification.setValue('status','received');
				grNotification.setValue('parent',current);
				grNotification.setValue('table', 'x_gfnll_onpage_notification');
				grNotification.setValue('document_id',eventContent.status.id);
				grNotification.setValue('short_description','Work notes entry');
				grNotification.setValue('priority',"LOW");

				grNotification.setValue('body',nbody);
				grNotification.setValue('recipients',nrecips);
				grNotification.setValue('reply_options',nreplies);
				grNotification.setValue('table',ntable);
				grNotification.setValue('template',template);
				notification.insert();
			}
		}
		else
		{

		}

})(current, previous);
