sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	gs.info("BR: is this thing on?");
	//logging
	function log(message,type){
		//LOGGING/////////
		var logTable = 'sys_script';
		var logID = 'c55e0919dba02380c822fcd6f496192a';
		var logMe = new x_gfnll_onpage.log();
		if(type=="info"){
		 logMe.info(message,type,logTable,logID);
		}
		if (type=="error"){
		  logMe.error(message,type,logTable,logID);
		}
	}
	log("Inbound event BR: Starting","error");
	log("Inbound event BR: Current value "+current.getValue("current"),"error");


	var json = current.getValue("current");
	var eventContent = JSON.parse(json);
	var eventStatus = eventContent.value.toString();
	var eventTimeStamp = eventContent.timestamp.toString();
	var eventReply = eventContent.replyoption.toString();
	var eventOPID = eventContent.opid.toString();
	var grActualNotification = new GlideRecord('x_gfnll_onpage_notification');
		grActualNotification.addQuery('sys_id', eventContent.id);
		grActualNotification.query();

		if (grActualNotification.next())
		{
			var workNotes = gs.getProperty('x_gfnll_onpage.can_update.work_notes');
			if(workNotes=="true")
			{


				var grNotification = new GlideRecord('x_gfnll_onpage_notification');
				grNotification.initialize();						
				grNotification.setValue('status','received');
				grNotification.setValue('parent',current);
				grNotification.setValue('table', 'x_gfnll_onpage_notification');
				grNotification.setValue('document_id',eventContent.id);
				grNotification.setValue('short_description','Work notes entry');
				grNotification.setValue('priority',"LOW");
				var userFullName = '';

								/*
sent,sending,send-ready,received,failed,error,cancelled,applied

"An OnPage message for Joe Smith was sent at 12:13pm"
---------------"An OnPage message for Joe Smith was delivered at 12:14pm"
---------------"Joe Smith replied "Assign" via OnPage."*/
				var message = "";
				var verb = "";
				switch(eventStatus)
					{
						case 'sent':
						case 'cancelled':
							verb = "was";
							break;
						case 'received':
							eventStatus='delivered';
							verb = "was";
							break;
						case 'sending':
						case 'send-ready':
							verb = "is";
							break;
						case 'failed':
						case 'error':
							verb="shows";
							break;

					}
				if(verb=='was' || verb=='is' || verb =='shows')
				{
					message = "An OnPage message for "+ userFullName + " "+verb+ " "+eventStatus+" at "+ eventTimeStamp;
				}
				else if(eventStatus=='applied')
				{
					message = userFullName + ' replied "' + eventReply + '" via OnPage at '+ eventTimeStamp;
				}

				grNotification.setValue('body',message);
				grNotification.insert();
			}
		}
		else
		{

		}

})(current, previous);
