sysauto_script.script:
sendNotif();

function sendNotif(){
	var method = gs.getProperty('x_gfnll_onpage.send_method');
	if(method=='batch'){
		var notifications = [];

		//bulk update to sending
		var gr1 = new GlideRecord('x_gfnll_onpage_notification');
			gr1.addQuery('status','send-ready');
			gr1.setLimit(100);
			gr1.query();
		var count = gr1.getRowCount();
		this.info("Ready notifications to process: "+count);
		if(count==0){
			return;
		}
		while(gr1.next()){
			notifications.push(gr1.getUniqueValue());
			gr1.setValue('status',"sending");
			gr1.update();
		}

		//collect notification values into array of messages
		var array = [];
		for(var i=0;i<notifications.length;i++){
			var gr = new GlideRecord('x_gfnll_onpage_notification');
				gr.get(notifications[i]);
			var table = gr.getValue('table'),
				doc = gr.getValue('document_id'),
				sd = gr.getValue('short_description'),
				body = gr.getValue('body'),
				recip = gr.getValue('recipients').split(","),
				priority = gr.getValue('priority'),
				options = gr.getValue('reply_options'),
				reply = '';
				if(options!=''&&options!=null){
					reply = options.split(",");
				}
			var template = '{"id": "'+doc+'","subject":"'+sd+'","body":"'+body+'","recipients":'+this.reformat(recip)+',"priority":"'+priority+'","possibleAnswers":'+this.reformat(reply)+'}';
			array.push(template);
		}
		this.info("Message array: "+array.toString());

		// send bulk message
		var resp = new x_gfnll_onpage.workflow().sendRestMessages('['+array.toString()+']',"e14129d8db68e340ee685e25ca961903");
		//process response
		var ans = resp.split(';'),
			status = ans[0],
			respBdy = ans[1];
		this.info("Response body is: "+respBdy);

		//update all records with status
		for(var k=0;k<notifications.length;k++){
			var gr2 = new GlideRecord('x_gfnll_onpage_notification');
				gr2.get(notifications[k]);
			if(status=="error"){
				gr2.setValue('status',"error");
				gr2.setValue('error',respBdy);
				gr2.update();
			}
			else{
				var json = JSON.parse(respBdy);
				var obj = json[k];
				var snID = obj.id,
					acptd = obj.accepted;
				if(acptd){
					gr2.setValue('status',status);
					gr2.setValue('onpage_id',snID);
					gr2.setValue('error','');
				}
				else{
					gr2.setValue('status',"error");
					gr2.setValue('error',obj.errorDescription);
				}
				gr2.update();
			}
		}
	}
}

function info(message){
	//LOGGING/////////
	var logType = "outbound";
	var logTable = 'sysauto_script';
	var id = "e14129d8db68e340ee685e25ca961903"; //this scheduled job
	var log = new x_gfnll_onpage.log();
		log.info(message,logType,logTable,id);
}
function reformat(array){
	if(array==''){
		return '[]';
	}
	var string,
		quotes = [];
	for(var i=0;i<array.length;i++){
		quotes.push('"'+array[i]+'"');
	}
	string = '['+quotes.toString()+']';
	return string;
}
